describe('Errors', () => {
  test('HTTP Errors should have a working toString()', () => {
    const status = 400;
    const msg = 'This is a bad request';
    const err = new errors.HTTPError(status, msg);
    expect(err.toString()).toBe(`HTTPError ${status}: ${msg}`);
  });
});